using System.Collections.Generic;

namespace ServerFleet.Models.Rest.Base
{
    public class BaseResponseCollection<T>    {        private string _errorMessage;        public int PageNumber { get; set; }        public int TotalPages { get; set; }        public int TotalRows { get; set; }        public int RowsPerPage { get; set; }        public bool Success { get; set; }        public string ErrorMessages        {            get => _errorMessage;            set            {                Success = false;                _errorMessage = value;            }        }        public List<T> Entities { get; set; }        public BaseResponseCollection()        {            Success = true;            Entities = new List<T>();        }        public BaseResponseCollection(List<T> data)        {            Success = true;            Entities = data;        }    }}